package Pack__demo;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		switch(1) {
		case 1:
			System.out.println('1');
			break;
		case 2:
			System.out.println('2');
		case 3:
			System.out.println('3');
		default :
			System.out.println("hihi default");
				
		}
		
		
		switch(3) {
		case 1:
			System.out.println('1');
		case 2:
			System.out.println('2');
		case 3:
			System.out.println('3');
				
		}
		
	}

}
